function gamepadLeftPressed(){return gamepad.justPressed(Phaser.Gamepad.XBOX360_DPAD_LEFT)||horizontalJustPressed(-1)}function gamepadUpPressed(){return gamepad.justPressed(Phaser.Gamepad.XBOX360_DPAD_UP)||verticalJustPressed(1)}function gamepadDownPressed(){return gamepad.justPressed(Phaser.Gamepad.XBOX360_DPAD_DOWN)||verticalJustPressed(-1)}
